        public virtual bool CheckImportData(string fileName, List<Spl_WareDetailsModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Name, "名称");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Code, "编码");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.BarCode, "条码");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.WareCategoryId, "存货分类");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Unit, "计量单位");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Lable, "存货标签");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.BuyPrice, "参考进价");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.SalePrice, "参考售价");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.RetailPrice, "零售价");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Remark, "备注");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Vender, "其他信息-厂家");
