				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Size, "尺码");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Weight, "重量");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.ComeFrom, "产地");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.UpperLimit, "最高库存");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.LowerLimit, "最低库存");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.PrimeCost, "参考成本");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Price1, "一级价格");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Price2, "二级价格");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Price3, "三级价格");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Price4, "四级价格");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Price5, "五级价格");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Photo1, "照片1");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Photo2, "照片2");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Photo3, "照片3");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Photo4, "照片4");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Photo5, "照片5");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.Enable, "状态");
				 excelFile.AddMapping<Spl_WareDetailsModel>(x => x.CreateTime, "CreateTime");
 
            //SheetName
            var excelContent = excelFile.Worksheet<Spl_WareDetailsModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new Spl_WareDetailsModel();
						 				  entity.Id = row.Id;
				  entity.Name = row.Name;
				  entity.Code = row.Code;
				  entity.BarCode = row.BarCode;
				  entity.WareCategoryId = row.WareCategoryId;
				  entity.Unit = row.Unit;
				  entity.Lable = row.Lable;
				  entity.BuyPrice = row.BuyPrice;
				  entity.SalePrice = row.SalePrice;
				  entity.RetailPrice = row.RetailPrice;
				  entity.Remark = row.Remark;
				  entity.Vender = row.Vender;
				  entity.Brand = row.Brand;
				  entity.Color = row.Color;
				  entity.Material = row.Material;
				  entity.Size = row.Size;
				  entity.Weight = row.Weight;
				  entity.ComeFrom = row.ComeFrom;
				  entity.UpperLimit = row.UpperLimit;
				  entity.LowerLimit = row.LowerLimit;
				  entity.PrimeCost = row.PrimeCost;
				  entity.Price1 = row.Price1;
				  entity.Price2 = row.Price2;
				  entity.Price3 = row.Price3;
				  entity.Price4 = row.Price4;
				  entity.Price5 = row.Price5;
				  entity.Photo1 = row.Photo1;
				  entity.Photo2 = row.Photo2;
				  entity.Photo3 = row.Photo3;
				  entity.Photo4 = row.Photo4;
				  entity.Photo5 = row.Photo5;
				  entity.Enable = row.Enable;
				  entity.CreateTime = row.CreateTime;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

