		 /// <summary>
        /// 校验Excel数据,这个方法一般用于重写校验逻辑
        /// </summary>
        public virtual bool CheckImportData(string fileName, List<SysTNumModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<SysTNumModel>(x => x.Num, "Num");
				 excelFile.AddMapping<SysTNumModel>(x => x.MinNum, "MinNum");
				 excelFile.AddMapping<SysTNumModel>(x => x.MaxNum, "MaxNum");
				 excelFile.AddMapping<SysTNumModel>(x => x.Day, "Day");
				 excelFile.AddMapping<SysTNumModel>(x => x.TabName, "TabName");
 
            //SheetName
            var excelContent = excelFile.Worksheet<SysTNumModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new SysTNumModel();
						 				  entity.Id = row.Id;
				  entity.Num = row.Num;
				  entity.MinNum = row.MinNum;
				  entity.MaxNum = row.MaxNum;
				  entity.Day = row.Day;
				  entity.TabName = row.TabName;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

