        public virtual bool CheckImportData(string fileName, List<SysUserModel> list,ref ValidationErrors errors )
        {
          
            var targetFile = new FileInfo(fileName);

            if (!targetFile.Exists)
            {

                errors.Add("导入的数据文件不存在");
                return false;
            }

            var excelFile = new ExcelQueryFactory(fileName);

            //对应列头
			 				 excelFile.AddMapping<SysUserModel>(x => x.UserName, "用户名");
				 excelFile.AddMapping<SysUserModel>(x => x.Password, "密码");
				 excelFile.AddMapping<SysUserModel>(x => x.TrueName, "真是名称");
				 excelFile.AddMapping<SysUserModel>(x => x.Card, "身份证");
				 excelFile.AddMapping<SysUserModel>(x => x.MobileNumber, "手机号码");
				 excelFile.AddMapping<SysUserModel>(x => x.PhoneNumber, "固话");
				 excelFile.AddMapping<SysUserModel>(x => x.QQ, "QQ");
				 excelFile.AddMapping<SysUserModel>(x => x.EmailAddress, "Email");
				 excelFile.AddMapping<SysUserModel>(x => x.OtherContact, "其他联系方式");
				 excelFile.AddMapping<SysUserModel>(x => x.Province, "省份");
				 excelFile.AddMapping<SysUserModel>(x => x.City, "城市");
				 excelFile.AddMapping<SysUserModel>(x => x.Village, "城镇街道");
				 excelFile.AddMapping<SysUserModel>(x => x.Address, "地址");
				 excelFile.AddMapping<SysUserModel>(x => x.State, "账户状态");
				 excelFile.AddMapping<SysUserModel>(x => x.CreateTime, "创建时间");
				 excelFile.AddMapping<SysUserModel>(x => x.CreatePerson, "创建人");
				 excelFile.AddMapping<SysUserModel>(x => x.Sex, "性别");
				 excelFile.AddMapping<SysUserModel>(x => x.Birthday, "生日");
				 excelFile.AddMapping<SysUserModel>(x => x.JoinDate, "加入时间");
				 excelFile.AddMapping<SysUserModel>(x => x.Marital, "婚姻");
				 excelFile.AddMapping<SysUserModel>(x => x.Political, "党派");
				 excelFile.AddMapping<SysUserModel>(x => x.Nationality, "民族");
				 excelFile.AddMapping<SysUserModel>(x => x.Native, "籍贯");
				 excelFile.AddMapping<SysUserModel>(x => x.School, "毕业学校");
				 excelFile.AddMapping<SysUserModel>(x => x.Professional, "就读专业");
				 excelFile.AddMapping<SysUserModel>(x => x.Degree, "学历");
				 excelFile.AddMapping<SysUserModel>(x => x.DepId, "部门");
				 excelFile.AddMapping<SysUserModel>(x => x.PosId, "职位");
				 excelFile.AddMapping<SysUserModel>(x => x.Expertise, "个人简介");
				 excelFile.AddMapping<SysUserModel>(x => x.JobState, "在职状况1在职，2离职");
				 excelFile.AddMapping<SysUserModel>(x => x.Photo, "照片");
				 excelFile.AddMapping<SysUserModel>(x => x.Attach, "附件");
				 excelFile.AddMapping<SysUserModel>(x => x.Lead, "上级领导ID");
				 excelFile.AddMapping<SysUserModel>(x => x.LeadName, "上级领导");
				 excelFile.AddMapping<SysUserModel>(x => x.IsSelLead, "自选领导");
				 excelFile.AddMapping<SysUserModel>(x => x.IsReportCalendar, "日程汇报");
				 excelFile.AddMapping<SysUserModel>(x => x.IsSecretary, "开启 小秘书");
 
            //SheetName
            var excelContent = excelFile.Worksheet<SysUserModel>(0);
            int rowIndex = 1;
            //检查数据正确性
            foreach (var row in excelContent)
            {
                var errorMessage = new StringBuilder();
                var entity = new SysUserModel();
						 				  entity.Id = row.Id;
				  entity.UserName = row.UserName;
				  entity.Password = row.Password;
				  entity.TrueName = row.TrueName;
				  entity.Card = row.Card;
				  entity.MobileNumber = row.MobileNumber;
				  entity.PhoneNumber = row.PhoneNumber;
				  entity.QQ = row.QQ;
				  entity.EmailAddress = row.EmailAddress;
				  entity.OtherContact = row.OtherContact;
				  entity.Province = row.Province;
				  entity.City = row.City;
				  entity.Village = row.Village;
				  entity.Address = row.Address;
				  entity.State = row.State;
				  entity.CreateTime = row.CreateTime;
				  entity.CreatePerson = row.CreatePerson;
				  entity.Sex = row.Sex;
				  entity.Birthday = row.Birthday;
				  entity.JoinDate = row.JoinDate;
				  entity.Marital = row.Marital;
				  entity.Political = row.Political;
				  entity.Nationality = row.Nationality;
				  entity.Native = row.Native;
				  entity.School = row.School;
				  entity.Professional = row.Professional;
				  entity.Degree = row.Degree;
				  entity.DepId = row.DepId;
				  entity.PosId = row.PosId;
				  entity.Expertise = row.Expertise;
				  entity.JobState = row.JobState;
				  entity.Photo = row.Photo;
				  entity.Attach = row.Attach;
				  entity.Lead = row.Lead;
				  entity.LeadName = row.LeadName;
				  entity.IsSelLead = row.IsSelLead;
				  entity.IsReportCalendar = row.IsReportCalendar;
				  entity.IsSecretary = row.IsSecretary;
 
                //=============================================================================
                if (errorMessage.Length > 0)
                {
                    errors.Add(string.Format(
                        "第 {0} 列发现错误：{1}{2}",
                        rowIndex,
                        errorMessage,
                        "<br/>"));
                }
                list.Add(entity);
                rowIndex += 1;
            }
            if (errors.Count > 0)
            {
                return false;
            }
            return true;
        }

